    private void botaoEnviarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_botaoEnviarMouseClicked
        
        String nome = tNome.getText();
        Integer codigo = Integer.parseInt(tCodigo.getText());
        float valor = Float.parseFloat(tValor.getText());
        
        produto = new Produto(nome, codigo, valor);
        
        JOptionPane.showMessageDialog(null,
                "Nome: " + produto.getNome() + "\n" +
                "Codigo: " + produto.getCodigo() + "\n" +
                "Valor: " + produto.getValor() + "\n" +
                "Preco: " + produto.getPreco()
        );
        
        
    }//GEN-LAST:event_botaoEnviarMouseClicked

